import React, { Component } from 'react';
import './App.css';
import Navbar from "./components/Navbar";
import Jumbotron from './components/Jumbotron';
import GameCard from './components/GameCard';
import fruits from "./fruits.json";

class App extends Component {

  state = {
    userScore: 0,
    topScore: 0,
    fruits,
    selectedFruits: [],
    message: "Let‘s play!"
  }

  checkTopScore = (score) => {
    if (score >= this.topScore) {
      this.setState({ topScore: score })
    };
  };

  selectFruit = (id) => {
    let selectedFruits = this.state.selectedFruits;
    if (selectedFruits.includes(id)) {
      this.setState({ message: "Sorry, try again." })
    } else {
      selectedFruits.push(id);
      this.setState({ userScore: this.userScore + 1, message: "Good!" })
      this.checkTopScore(this.state.userScore);
    };
  };

  shuffleFruit = () => {
    let fruitArray = this.state.fruits;
    function shuffle(array) {
      var i = 0,
        j = 0,
        temp = null
      for (i = array.length - 1; i > 0; i -= 1) {
        j = Math.floor(Math.random() * (i + 1))
        temp = array[i]
        array[i] = array[j]
        array[j] = temp
      };
    }
    shuffle(fruitArray);
    this.setState({
      fruits: fruitArray
    });
  };

  handleClick = (id) => {
    this.selectFruit(id);
    this.shuffleFruit();
  }

  render() {
    return (
      <div>
        <Navbar
          message={this.state.message}
          userScore={this.state.userScore}
          topScore={this.state.topScore}
        />
      <Jumbotron />
        <div className="container">
          <div className="row">
              {this.state.fruits.map(fruit => (
                  <GameCard
                    key={fruit.id}
                    id={fruit.id}
                    name={fruit.name}
                    image={fruit.image}
                    select={this.handleClick}
                  />
              ))}
          </div>
        </div>
      </div>
    );
  }
};

export default App;
