import React, { Component } from 'react';
import './App.css';
import Navbar from "./components/Navbar";
import Jumbotron from './components/Jumbotron';
import Game from './components/Game';
import fruits from "./fruits.json";

class App extends Component {



  state = {
    userScore: 0,
    topScore: 0,
    fruits,
    shuffleArray: function (array) {
      for (let i = array.length - 1; i > 0; i--) {
          const j = Math.floor(Math.random() * (i + 1));
          [array[i], array[j]] = [array[j], array[i]];
      }
    },
    shuffledFruit: function() {
      console.log(this.fruits)
      return this.shuffleArray(this.fruits);
    }
  }

  printArray = () => {console.log(this.state.fruits)};

  // shuffleFruit = () => {
  //   const fruits = this.shuffleArray(this.state.fruits);
  //   this.setState({ fruits });
  //   console.log(this.state.fruits)
  // };

  render() {
    return (
      <div>
        <Navbar />
        <Jumbotron />
        <Game 
          print={this.printArray}
          fruitArray={this.state.shuffledFruit()} 
          shuffle={this.shuffleFruit}
        />
      </div>
    );
  }
}

export default App;
